import * as React from "react";
import ItemCount from "../ItemCount/ItemCount";
import { useCart } from "../../context/CartContext";



const ItemDetail = ({ id,title, description, image, price }) => {

  const { addItem } = useCart();
  const [count, setCount] = React.useState(0);

  const btnAdd = () => {
    setCount((count) => count + 1);

  };
  const btnSubs = () => {
    setCount((count) => count - 1);
  };

  const addToCart = () => {
    const dataToSend = {
      id:id,
      title: title,
      description: description,
      qty: count,
      price: price,
      img:image
    }

    addItem(dataToSend);
  };


  return (
    <section>
  <h2>Estilo C</h2>
    <div class="card estilo-c">
      <a href="#">
        <div class="img-container">
          <img src="https://images.pexels.com/photos/4465124/pexels-photo-4465124.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="producto 1"/>
        </div>
       </a>
      <div class="info-container">
        <h3>Crema verde</h3>
        <strong>$60</strong>
        <span class="rating">★★★★☆</span>
        <a href="#" class="add-cart">Añadir al carrito</a>
      </div>
    </div>
    <div class="card estilo-c">
      <a href="#">
        <div class="img-container">
          <img src="https://images.pexels.com/photos/4202924/pexels-photo-4202924.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="producto 1"/>
          <span class="promo">15% de descuento</span>
        </div>
        </a>
      <div class="info-container">
        <h3>Jabón de baño</h3>
        <strong>$30</strong>
        <span class="rating">★★★☆☆</span>
        <a href="#" class="add-cart">Añadir al carrito</a>
      </div>
    </div>
    <div class="card estilo-c">
      <a href="#">
        <div class="img-container">
          <img src="https://images.pexels.com/photos/4465830/pexels-photo-4465830.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260" alt="producto 1"/>
          <span class="promo">15% de descuento</span>
        </div>
        </a>
      <div class="info-container">
        <h3>Aceite rejuvenecedor</h3>
        <strong>$120</strong>
        <span class="rating">★★★★★</span>
        <a href="#" class="add-cart">Añadir al carrito</a>
      </div>
    </div>
</section>
  );
};

export default ItemDetail;
