import * as React from "react";
import { useCart } from "../context/CartContext";


const Cart = () => {
  const { cart } = useCart();
  console.log(cart)
  return (
    <>

      <div style={{ minHeight: "calc(100vh - 100px)" }}>
        <h1>Cart</h1>
      </div>
      {cart?.map((item) => {
      return(
       <div>
<tr>
                <td><img src={item.image} style="height: 150px; width: 150px"/> </td>
                <td>{item.title}</td>
                <td>En stock</td>
                <td>
                    <div class="row">
                        <div class="col-2">
                        <a href="#"><i class="fas fa-minus"></i></a>
                        </div>
                        <div class="col-8">
                        <input class="form-control qty" style="background-color: #EFE7E1;" type="number" value="1" min="0" />
                        </div>
                        <div class="col-2">
                        <a href="#" ><i class="fas fa-plus"></i></a>
                        </div>
                    </div>
                </td>
                <td class="text-right">$<span name="price">${item.price.precio}</span></td>
                <td class="text-right"><button class="btn btn-sm btn-danger"><i class="fa fa-trash"></i> </button> </td>
            </tr>
       </div>
    )
        })}
      </>
    );
  };
export default Cart;
