import * as React from "react";
import { useCart } from "../context/CartContext";
import ItemDetail from "../components/ItemDetail/ItemDetail";

const Cart = () => {
  const { cart } = useCart();
  console.log(cart)  
  return (
    <>

      <div style={{ minHeight: "calc(100vh - 100px)" }}>
        <h1>Cart</h1>
      </div>
      {cart?.map((item) => {
          return (
            <>
              id={item.id}
              title={item.title}
              description={item.description}
              image={item.image}
              price={item.price}

              <tr>
                <td><img src={item.image} style="height: 150px; width: 150px"/> </td>
                <td>{carrito[i].nombre}</td>
                <td>En stock</td>
                <td>
                    <div class="row">
                        <div class="col-2">
                        <a href="#" id="removeQty{carrito[i].id}"><i class="fas fa-minus"></i></a>
                        </div>
                        <div class="col-8">
                        <input class="form-control qty" style="background-color: #EFE7E1;" type="number" value="1" min="0" id="qtyProducto{carrito[i].id}" />
                        </div>
                        <div class="col-2">
                        <a href="#" id="addQty{carrito[i].id}"><i class="fas fa-plus"></i></a>
                        </div>
                    </div>
                </td>
                <td class="text-right"><span name="price">{carrito[i].precio}</span></td>
                <td class="text-right"><button class="btn btn-sm btn-danger" id="eliminarProducto${i}"><i class="fa fa-trash"></i> </button> </td>
            </tr>
            </>
          );
        })}

    </>

    );
  };
  export default Cart;
