import * as React from "react";
import ItemCount from "../ItemCount/ItemCount";
import { useCart } from "../../context/CartContext";



const ItemDetail = ({ id,title, description, image, price }) => {

  const { addItem } = useCart();
  const [count, setCount] = React.useState(0);

  const btnAdd = () => {
    setCount((count) => count + 1);

  };
  const btnSubs = () => {
    setCount((count) => count - 1);
  };

  const addToCart = () => {
    const dataToSend = {
      id:id,
      title: title,
      description: description,
      qty: count,
      price: price,
      img:image
    }

    addItem(dataToSend);
  };


  return (
<div className="cardContainer col-lg-4">
    <aside className="product-card">
    <header>
      <a target="_blank" href="#"><img src={image} alt={title} className="cardImg"/></a>
      <h1 className="cardTitle">{title}</h1>
      <h2 className="cardPrice">$ {price}</h2>
    </header>
    <div className="product-bio">
      <p className="cardDescription">{description}</p>
      <button className="cart">Add to cart</button>
    </div>
  </aside>
  <ItemCount count={count}
      btnSubs={btnSubs}
      btnAdd={btnAdd}
      comprar={addToCart}
      />
  </div>
  );
};

export default ItemDetail;
